  head() %>% 
  gt() %>% 
  basic_theme(
    column_labels.background.color = "red",
    table.font.size = px(12),
    column_labels.font.size = px(20),
    row.striping.background_color = "#9678b6",
    heading.align = "left",
    heading.title.font.size = px(30)
  ) %>% 
  opt_row_striping() %>% 
  tab_header(title = "Important table with theme")
